import React from 'react';
import Navigation from './components/navigation/navigation';
import Logo from './components/logo/logo';
import ImageLinkForm from './components/imageLinkForm/imageLinkForm';
import Rank from './components/rank/rank';
import FoodRecognition from './components/foodRecognition/foodRecognition';
import './App.css';
import Clarifai from 'clarifai';


const app = new Clarifai.App({
  apiKey: '170c850e3cb041068eb526c625cdfc11'
 });

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {}
    }
  }

  displayFoodNames = (data) => {
    const clarifaiFood = data.outputs[0].data.concepts[0].name;
    const image = document.getElementById('inputImage');
    const width = Number(image.width);
    const height = Number(image.height);
    console.log(clarifaiFood, width, height);
  }; 

  onInputChange = (event) => {
    this.setState({input: event.target.value});
  };

  onSubmit = () => {
    this.setState({imageUrl: this.state.input});
    app.models
      .predict(
        Clarifai.FOOD_MODEL,
        this.state.input)
      .then(response => this.displayFoodNames(response))
      .catch(err => {
        console.log(err);
      })
  }; 

  render() {
    return (
      <div className="App">
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
          onInputChange={this.onInputChange} 
          onSubmit={this.onSubmit}/>
        <FoodRecognition imageUrl={this.state.imageUrl}/>
      </div>
    );
  };
};

export default App;
